package com.martin.lc.locked;
/**
 * The API: int read4(char *buf) reads 4 characters at a time from a file.

The return value is the actual number of characters read. For example, it returns 3 if there is only 3 characters left in the file.

By using the read4 API, implement the function int read(char *buf, int n) that reads n characters from the file.

Note:
The read function may be called multiple times.
 * */
public class Read4KII {
	 public int read4(char[] buf){
		 return 1;
	 }
	 public boolean eof = false;
	    public char[] buffer = new char[4];
	    public int index = 0; //at this index in buf, char will be injected
	    public int bufferRemain = 0;//how many stuff still remain in buffer
	    public int validTill = 0;
	    
	    public int read(char[] buf, int n) {
	        int need = n;
	        
	        while(need > 0){
	            if(eof) break;
	            int takeCnt = 0;
	            //get from remain if any
	            if(bufferRemain!=0){
	                if(need >= bufferRemain){
	                    //take all remain stuff
	                    takeCnt = bufferRemain;
	                    for(int i = validTill - bufferRemain;i<validTill;i++){
	                        buf[index++] = buffer[i];
	                    }
	                    bufferRemain = 0;
	                    validTill = 0;
	                }else{
	                    takeCnt = need;
	                    for(int i=validTill-bufferRemain;i< validTill - bufferRemain + takeCnt;i++){
	                        buf[index++] = buffer[i];
	                    }
	                    bufferRemain  -= takeCnt;
	                }
	            }else{
	                //no remain, get from read4
	                int got = read4(buffer);
	                if(got<4) {
	                    validTill = got;
	                    eof = true;
	                }
	                
	                if(need >= got){
	                    takeCnt = got;
	                    //take them all, bufferRemain stays 0
	                    for(int i=0;i<takeCnt;i++){
	                        buf[index++] = buffer[i];
	                    }
	                }else{
	                    //take portion of it, and update remainBuffer,validTill
	                    takeCnt = need;
	                    for(int i=0;i<takeCnt;i++){
	                        buf[index++] = buffer[i];
	                    }
	                    bufferRemain = got - need;
	                }
	            }
	            
	            need -= takeCnt;
	        }
	        return n - need;
	    }
	    
	    public static boolean isOneEditDistance(String s, String t) {
	        if(s == null || t == null) return false;
	        int len1 = s.length();
	        int len2 = t.length();
	        if(s.equals(t)) 
	        	return false;
	        else 
	        	len1 = 1;
			if(Math.abs(len1-len2) >=2 ) return false;
	        int[][] f = new int[len1+1][len2+1];
	        for(int i=1;i<=len2;i++) f[0][i] = i;
	        for(int i=1;i<=len1;i++) f[i][0] = i;
	        
	        //f[i+1][j+1]
	        for(int i=0;i<len1;i++){
	            for(int j=0;j<len2;j++){
	                f[i+1][j+1] = f[i+1][j] + 1;
	                if(s.charAt(i) == t.charAt(j)){
	                    f[i+1][j+1] = Math.min(f[i+1][j+1], f[i][j]);
	                }else{
	                    f[i+1][j+1] = Math.min(f[i][j], Math.min(f[i+1][j],f[i][j+1])) + 1;
	                }
	            }
	        }
	        return f[len1][len2] ==  1;
	    }
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		isOneEditDistance("",
				"");
	}

}
